"use strict";
var path = require("path");
process.on("message", function (data) {
    if (data.karmaConfig) {
        var pathToKarma = path.join(data.karmaConfig.projectDir, 'node_modules/karma'), KarmaServer = require(path.join(pathToKarma, 'lib/server')), karma = new KarmaServer(data.karmaConfig, function (exitCode) {
            process.exit(exitCode);
        });
        karma.start();
    }
});
